public class Question1 {
    //area of rectangle
    public static void main(String[] args){
        Question1 q1 = new Question1();
        int area =  q1.areaOfRectangle();
    }
    public int areaOfRectangle(){
        int length = 100;
        int breadth = 50;
        return length * breadth;
    }
}
